"use strict";

var searchBtn = document.querySelector('.search-btn');
var searchBlock = document.querySelector('.header__search');
var searchBtnClose = document.querySelector('.search__btn-close');
var contactsBtnClose = document.querySelector('.contacts__btn-close');
var contactsMapDescr = document.querySelector('.contacts__map-descr');
var contactsMapMarker = document.querySelector('.contacts__map-marker');
var navBtn = document.querySelector('.nav-btn');
var navBlock = document.querySelector('.header__burger');
var navBtnClose = document.querySelector('.nav__btn-close');
var navLinks = document.querySelectorAll('.header__burger .nav__link');
searchBtn.addEventListener('click', function () {
  searchBlock.classList.toggle('header__search_active');
});
searchBtnClose.addEventListener('click', function () {
  searchBlock.classList.remove('header__search_active');
});
contactsBtnClose.addEventListener('click', function () {
  contactsMapDescr.classList.remove('contacts__map-descr_open');
});
contactsMapMarker.addEventListener('click', function () {
  contactsMapDescr.classList.toggle('contacts__map-descr_open');
});
navBtn.addEventListener('click', function () {
  navBlock.classList.toggle('header__burger_open');
  document.body.classList.toggle('stop-scroll');
});
navBtnClose.addEventListener('click', function () {
  navBlock.classList.remove('header__burger_open');
  document.body.classList.remove('stop-scroll');
});
navLinks.forEach(function (elem) {
  elem.addEventListener('click', function () {
    navBlock.classList.remove('header__burger_open');
    document.body.classList.remove('stop-scroll');
  });
});
